import React from 'react';
import ChatList from '../ChatList/ChatList';
import styles from './Style';
import { Button, withStyles } from '@material-ui/core';
const firebase = require("firebase");

class Dashbord extends React.Component {
    constructor(){
        super();
        this.state= {
            selectedChat: null,
            newChatFormVisible: false,
            email: null,
            chats: []
        }
    }
render(){
    const { classes } = this.props;
return(
    <div>
        <ChatList history={this.props.history}
        newChatBtnFunction={this.props.newChatBtnClicked}
        selectChatFunction={this.props.selectChat}
        chats={this.state.chats}
        userEmail = {this.state.email}
        selectChatIndex={this.state.selectedChat} 
        ></ChatList>
        <Button className={classes.signOutBtn} onClick={this.signOut}>Вийти</Button>
    </div>
)
}
signOut = () => 
selectChat = (chatIndex) =>{
    console.log('h', chatIndex);
}
newChatBtnClicked = () => this.setState({newChatFormVisible: true, selectedChat: null});
componentDidMount = () =>{
    firebase.auth().onAuthStateChanged(async _usr =>{
        if(!_usr)
        this.props.history.push('/login');
        else{
            await firebase
            .firestore()
            .collection('chats')
            .where('users', 'array-contains', _usr.email)
            .onSnapshot(async res =>{
                const chats = res.docs.map(_doc => _doc.data());
                await this.setState({
                    email: _usr.email,
                    chats: chats
                });
                console.log(this.state)
            })
        }
    })
}
}

export default withStyles(styles)(Dashbord);
