import { Link } from 'react-router-dom';
import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Paper from '@material-ui/core/Paper';
import withStyles from '@material-ui/core/styles/withStyles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import styles from './Style';
const firebase = require("firebase");


class Singup extends React.Component {

    constructor(){
        super();
        this.state={
            email: null,
            password: null,
            passwordConfirmation: null,
            signupError: ''
        }
    }

render(){
    const { classes } = this.props;

return(
    <main className={classes.main}>
        <CssBaseline></CssBaseline>
        <Paper className={classes.paper}>
            <Typography component='h1' variant='h5'>
                Зареєструватись
            </Typography>
            <form onSubmit={(e) => this.submitSignup(e)} className={classes.form}>
                <FormControl require fullWidth margin='normal' >
                    <InputLabel htmlFor='signup-email-input'>
                        Введіть свій email
                    </InputLabel>
                    <Input onChange={(e) => this.userTyping('email', e)} autoComplete='email'autoFocus id='signup-email-input' ></Input>
                </FormControl>
                <FormControl require fullWidth margin='normal' >
                    <InputLabel htmlFor='signup-password-input'>
                        Придумайте пароль
                    </InputLabel>
                    <Input type='password' onChange={(e) => this.userTyping('password', e)} autoFocus id='signup-password-input' ></Input>
                </FormControl>
                <FormControl require fullWidth margin='normal' >
                    <InputLabel htmlFor='signup-password=confirmation-input'>
                        Повторіть пароль 
                    </InputLabel>
                    <Input type='password' onChange={(e) => this.userTyping('passwordConfirmation', e)} autoFocus id='signup-password=confirmation-input' ></Input>
                </FormControl>
                <Button type='submit' className={classes.submit} fullWidth variant='contained' color='primary'>Зареєструватись</Button>
            </form>
            {
                this.state.signupError ?
                <Typography className={classes.signupError}component='h5' variant='h6'>
                    {this.state.signupError}
                </Typography> :
                null
            }
            <Typography className={classes.hasAccountHeader} component='h5' variant='h6'>Ви вже маєте акаунт</Typography>
            <Link to='/login' className={classes.logInLink}>Увійти</Link>

        </Paper>

    </main>
)
}
formIsValidation = () => this.state.password === this.state.passwordConfirmation;

userTyping= (type, e) =>{
 switch (type){
     case 'email':
         this.setState({email: e.turget.value});
     break;
     case 'password':
        this.setState({password: e.turget.value});
    break;
    case 'passwordConfirmation':
        this.setState({passwordConfirmation: e.turget.value});
    break;
     default:
         break;
 }
};
submitSignup = (e) => {
    e.preventDefault();
    if (!this.formIsValidation()){
        this.setState({signupError: 'паролі не співпадають'});
    }
    куе
    console.log("fnkj");
};
}
export default withStyles(styles)(Singup);
