import { Link } from 'react-router-dom';
import React from 'react';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Paper from '@material-ui/core/Paper';
import withStyles from '@material-ui/core/styles/withStyles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import styles from './Style';
const firebase = require("firebase");

class Login extends React.Component {

    constructor() {
        super();
        this.state = {
          email: null,
          password: null,
          serverError: false
        };
      }
    
      render() {
    
        const { classes } = this.props;
    
        return (
          <main className={classes.main}>
            <CssBaseline/>
            <Paper className={classes.paper}>
              <Typography component="h1" variant="h5">
                Увійти!
              </Typography>
              <form onSubmit={(e) => this.submitLogin(e)} className={classes.form}>
                <FormControl required fullWidth margin='normal'>
                  <InputLabel htmlFor='login-email-input'>Введіть свій емаіл</InputLabel>
                  <Input autoComplete='email' autoFocus onChange={(e) => this.userTyping('email', e)} id='login-email-input'></Input>
                </FormControl>
                <FormControl required fullWidth margin='normal'>
                  <InputLabel htmlFor='login-password-input'>Введіть свій пароль</InputLabel>
                  <Input autoComplete="current-password" type="password" onChange={(e) => this.userTyping('password', e)} id='login-password-input'></Input>
                </FormControl>
                <Button type='submit' fullWidth variant='contained' color='primary' className={classes.submit}>Log In</Button>
              </form>
              { this.state.serverError ? 
                <Typography className={classes.errorText} component='h5' variant='h6'>
                  Incorrect Login Information
                </Typography> :
                null
              }
              <h5 className={classes.noAccountHeader}>Ще не маєте акаунта?</h5>
              <Link className={classes.signUpLink} to='/singup'>Зареєструватися</Link>
            </Paper>
          </main>
        );
      }
    
      userTyping = (whichInput, event) => {
        switch (whichInput) {
          case 'email':
            this.setState({ email: event.target.value });
            break;
    
          case 'password':
            this.setState({ password: event.target.value });
            break;
    
          default:
            break;
        }
      }
    
      submitLogin = async (e) => {
        e.preventDefault(); 
    
        await firebase
          .auth()
          .signInWithEmailAndPassword(this.state.email, this.state.password)
          .then(() => {
            this.props.history.push('/dashbord');
          }, err => {
            this.setState({ serverError: true });
            console.log('Error logging in: ', err);
          });
      };
    
    }
    
export default withStyles(styles)(Login);
