import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import styles from './Style';
import Divider from '@material-ui/core/Divider';
import Button from '@material-ui/core/Button';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import NotificationImportant from '@material-ui/icons/NotificationImportant';

class ChatList extends React.Component {

  render() {
    const { classes } = this.props;
      return(
        <main className={classes.root}>
            <Button variant ="contained"
            fullWidth
            color="primary"
            className={classes.NewChatBtn}
            onClick={this.newChat}>
            </Button>
            <List>
                {
                    this.props.chats.map((_chat, _index) =>{
                        return(
                            <ListItem onClick={() => this.selectChat(_index)}
                            className={classes.ListItem}
                            selected={this.props.selectChatIndex === _index} 
                            alignItems='flex-start'>
                             <ListItemAvatar>
                                 <Avatar alt='Remy Sharp'>
                                     {
                                         _chat.users.filter(_user => _user !== this.props.userEmail)[0].split('')[0]
                                     }
                                 </Avatar>
                                 <ListItemText primary={_chat.users.filter(_user => _user !== this.props.userEmail)[0]}
                                 secondary={
                                     <React.Fragment>
                                         <Typography component='span' color='textPrimary'>
                                             {
                                                 _chat.messages[_chat.messages.length - 1].messages.substring
                                             }

                                         </Typography>
                                     </React.Fragment>
                                 } >

                                 </ListItemText>
                             </ListItemAvatar>

                            </ListItem>
                        )
                    })
                }
            </List>

        </main>
      )
  }
  newChat =() =>{
      console.log('new chat click')
  }
  selectChat = (index) =>{
      console.log('select chat', index);
  }
}
export default withStyles(styles)(ChatList);
